import React, { useEffect, useState } from 'react';
import Login from './Login';
import AddCitizenForm from './AddCitizenForm';
import SearchCitizen from './SearchCitizen';
import DeleteCitizen from './DeleteCitizen'; // <-- quan tr·ªçng
import { getToken, setToken, apiGet } from './api';

export default function App() {
  const [user, setUser] = useState(null);
  const [activeTab, setActiveTab] = useState('add'); // add | search | delete
  const token = getToken();

  useEffect(() => {
    // ch·ªâ ping th·ª≠ BE; kh√¥ng b·∫Øt bu·ªôc
    (async () => { try { await apiGet('/api/health'); } catch {} })();
  }, []);

  const handleLoginSuccess = ({ token, user }) => {
    setToken(token);
    setUser(user);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
  };

  if (!token || !user) return <Login onLoginSuccess={handleLoginSuccess} />;

  return (
    <div style={{ padding: 20, maxWidth: 960, margin: '0 auto' }}>
      <div style={{ display:'flex', justifyContent:'space-between', alignItems:'baseline' }}>
        <h2>H·ªá th·ªëng Qu·∫£n l√Ω C√¥ng d√¢n</h2>
        <div>
          <span style={{ marginRight: 12 }}>Xin ch√†o, <b>{user.fullName}</b></span>
          <button onClick={handleLogout}>ƒêƒÉng xu·∫•t</button>
        </div>
      </div>

      {/* Tabs ƒë∆°n gi·∫£n */}
      <div style={{ marginTop: 16, display:'flex', gap:8 }}>
        <button onClick={()=>setActiveTab('add')}
                style={{ fontWeight: activeTab==='add' ? 'bold' : 'normal' }}>
          ‚ûï Th√™m c√¥ng d√¢n
        </button>
        <button onClick={()=>setActiveTab('search')}
                style={{ fontWeight: activeTab==='search' ? 'bold' : 'normal' }}>
          üîé Tra c·ª©u c√¥ng d√¢n
        </button>
        <button onClick={()=>setActiveTab('delete')}
                style={{ fontWeight: activeTab==='delete' ? 'bold' : 'normal' }}>
          üóëÔ∏è X√≥a c√¥ng d√¢n
        </button>
      </div>

      <div style={{ marginTop: 20 }}>
        {activeTab === 'add' && <AddCitizenForm />}
        {activeTab === 'search' && <SearchCitizen />}
        {activeTab === 'delete' && <DeleteCitizen />}  {/* <-- render ·ªü ƒë√¢y */}
      </div>
    </div>
  );
}
