// src/AddCitizenForm.js
import React, { useState } from 'react';
import {
  Card, CardContent, Typography, Grid, TextField,
  FormControlLabel, Checkbox, Button, Alert, Stack, Box, FormGroup, Divider
} from '@mui/material';
import { apiPost } from './api';

export default function AddCitizenForm() {
  const [form, setForm] = useState({
    nationalId: '',
    fullName: '',
    dateOfBirth: '',
    gender: '',
    areaName: '',
    areaType: '',
    householdAddress: '',
    isHead: false,
    hasCriminalRecord: false,
    setWanted: false,
    setQuanChe: false
  });
  const [msg, setMsg] = useState('');
  const [err, setErr] = useState('');

  const change = (e) => {
    const { name, type, value, checked } = e.target;
    setForm(f => ({ ...f, [name]: type === 'checkbox' ? checked : value }));
  };

  const toggleHasCriminal = (e) => {
    const checked = e.target.checked;
    setForm(f => ({
      ...f,
      hasCriminalRecord: checked,
      setWanted: checked ? f.setWanted : false,
      setQuanChe: checked ? f.setQuanChe : false
    }));
  };

  const submit = async (e) => {
    e.preventDefault();
    setMsg(''); setErr('');

    if ((form.setWanted || form.setQuanChe) && !form.hasCriminalRecord) {
      setErr('Vui lòng tích "Có tiền án/tiền sự" trước khi chọn Truy nã hoặc Quản chế.');
      return;
    }

    try {
      const data = await apiPost('/api/citizens', form);
      setMsg(`Thêm thành công (CitizenID: ${data?.data?.CitizenID ?? 'N/A'})`);
      setForm({
        nationalId: '', fullName: '', dateOfBirth: '', gender: '',
        areaName: '', areaType: '', householdAddress: '',
        isHead: false, hasCriminalRecord: false, setWanted: false, setQuanChe: false
      });
    } catch (error) { setErr(error.message); }
  };

  return (
    <Card>
      <CardContent>
        <Typography variant="h6" gutterBottom>Nhập thông tin</Typography>

        <form onSubmit={submit}>
          <Stack spacing={2}>
            {msg && <Alert severity="success">{msg}</Alert>}
            {err && <Alert severity="error">{err}</Alert>}

            <Grid container spacing={2}>
              {/* Hàng 1: CCCD | Họ tên */}
              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth required
                  label="CCCD * *"
                  name="nationalId"
                  inputProps={{ maxLength: 12 }}
                  value={form.nationalId}
                  onChange={change}
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth required
                  label="Họ tên * *"
                  name="fullName"
                  value={form.fullName}
                  onChange={change}
                />
              </Grid>

              {/* Hàng 2: Ngày sinh | Giới tính (TextField select để full width) */}
              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  type="date"
                  label="Ngày sinh"
                  name="dateOfBirth"
                  InputLabelProps={{ shrink: true }}
                  value={form.dateOfBirth}
                  onChange={change}
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  select
                  label="Giới tính"
                  name="gender"
                  value={form.gender}
                  onChange={(e)=>change({ target:{ name:'gender', value:e.target.value } })}
                >
                  <option value=""></option>
                  <option value="Nam">Nam</option>
                  <option value="Nữ">Nữ</option>
                  <option value="Khác">Khác</option>
                </TextField>
              </Grid>

              {/* Hàng 3: Khu vực | Loại khu vực (TextField select để full width) */}
              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth required
                  label="Khu vực (Area Name) * *"
                  name="areaName"
                  value={form.areaName}
                  onChange={change}
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth required
                  select
                  label="Loại khu vực (Area Type) * *"
                  name="areaType"
                  value={form.areaType}
                  onChange={(e)=>change({ target:{ name:'areaType', value:e.target.value } })}
                >
                  <option value=""></option>
                  <option value="Thôn">Thôn</option>
                  <option value="Tổ">Tổ</option>
                </TextField>
              </Grid>

              {/* Hàng 4: Địa chỉ hộ (full) */}
              <Grid item xs={12}>
                <TextField
                  fullWidth required
                  label="Địa chỉ hộ * *"
                  name="householdAddress"
                  value={form.householdAddress}
                  onChange={change}
                />
              </Grid>

              {/* Hàng 5: Chủ hộ | Tình trạng pháp lý */}
              <Grid item xs={12} md={6}>
                <FormControlLabel
                  control={<Checkbox name="isHead" checked={form.isHead} onChange={change} />}
                  label="Chủ hộ"
                />
              </Grid>

              <Grid item xs={12} md={6}>
                <Box sx={{ border: '1px solid', borderColor: 'divider', borderRadius: 2, p: 2 }}>
                  <Typography variant="subtitle2" sx={{ mb: 1 }}>Tình trạng pháp lý</Typography>
                  <FormGroup>
                    <FormControlLabel
                      control={<Checkbox checked={form.hasCriminalRecord} onChange={toggleHasCriminal} />}
                      label="Có tiền án/tiền sự"
                    />
                    <Divider sx={{ my: 1 }} />
                    <FormControlLabel
                      control={
                        <Checkbox
                          name="setWanted"
                          checked={form.setWanted}
                          onChange={change}
                          disabled={!form.hasCriminalRecord}
                        />
                      }
                      label="Truy nã"
                    />
                    <FormControlLabel
                      control={
                        <Checkbox
                          name="setQuanChe"
                          checked={form.setQuanChe}
                          onChange={change}
                          disabled={!form.hasCriminalRecord}
                        />
                      }
                      label="Quản chế"
                    />
                  </FormGroup>
                </Box>
              </Grid>
            </Grid>

            <Button type="submit" variant="contained" size="large">
              Thêm công dân
            </Button>
          </Stack>
        </form>
      </CardContent>
    </Card>
  );
}
