import React, { useState } from 'react';
import { apiGet } from './api';

export default function SearchCitizen() {
  const [cccd, setCccd] = useState('');
  const [data, setData] = useState(null);
  const [err, setErr] = useState('');

  const search = async () => {
    setErr(''); setData(null);
    if (!cccd) return;
    try {
      const res = await apiGet(`/api/citizens/${cccd}`);
      setData(res);
    } catch (e) { setErr(e.message); }
  };

  return (
    <div>
      <div style={{ display:'flex', gap:8 }}>
        <input placeholder="Nhập CCCD" value={cccd} onChange={e=>setCccd(e.target.value)} />
        <button onClick={search}>Tra cứu</button>
      </div>
      {err && <p style={{ color:'red' }}>{err}</p>}
      {data && (
        <div style={{ marginTop:12, border:'1px solid #ddd', padding:12 }}>
          <h4>Thông tin công dân</h4>
          <p><b>Họ tên:</b> {data.FullName}</p>
          <p><b>CCCD:</b> {data.NationalID}</p>
          <p><b>Ngày sinh:</b> {data.DateOfBirth ? String(data.DateOfBirth).substring(0,10) : ''}</p>
          <p><b>Giới tính:</b> {data.Gender}</p>
          <p><b>Địa chỉ hộ:</b> {data.HouseholdAddress}</p>
          <p><b>Khu vực:</b> {data.AreaType} {data.AreaName}</p>
          <p><b>Chủ hộ:</b> {data.IsHeadOfHousehold ? 'Có' : 'Không'}</p>
          <p><b>Trạng thái hiệu lực:</b> {data.AllActiveStatuses || 'Không'}</p>
          {data.AlertText && <p style={{ color:'red' }}><b>Cảnh báo:</b> {data.AlertText}</p>}
        </div>
      )}
    </div>
  );
}
